class Solution:
    def threeSum(self, nums, target):
        nums.sort()
        res = sum(nums[:3])


        for i,a in enumerate(nums):
            l,r = i+1,len(nums)-1
            while l<r:
                tsum = a+nums[l]+nums[r]
                if abs(res-target)>abs(tsum-target):
                    res = tsum
                if target<tsum:
                    r -= 1
                else:
                    l += 1
        return res

